  useEffect(() => {
    const response = fetch('https://react-post-a0184-default-rtdb.europe-west1.firebasedatabase.app/meals.json');
  }, [])

  const data = response.json();

      const loadedMeals = [];

      for (const key in data) {
        loadedMeals.push({
          id: key,
          name: data[key].name,
          descritpion: data[key].description,
          price: data[key].price
        })
      };

  const mealsList = DUMMY_MEALS.map((meal) => (
    <MealItem
      id={meal.id}
      key={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
